// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

// Interfaces
import {IOwnableTwoSteps} from "../interfaces/IOwnableTwoSteps.sol";

/**
 * @title OwnableTwoSteps
 * @notice This contract offers transfer of ownership in two steps with potential owner
 *         having to confirm the transaction to become the owner.
 *         Renouncement of the ownership is also a two-step process since the next potential owner is the address(0).
 * @author LooksRare protocol team (ðŸ‘€,ðŸ’Ž)
 */
abstract contract OwnableTwoSteps is IOwnableTwoSteps {
    /**
     * @notice Address of the current owner.
     */
    address public owner;

    /**
     * @notice Address of the potential owner.
     */
    address public potentialOwner;

    /**
     * @notice Ownership status.
     */
    Status public ownershipStatus;

    /**
     * @notice Modifier to wrap functions for contracts that inherit this contract.
     */
    modifier onlyOwner() {
        _onlyOwner();
        _;
    }

    /**
     * @notice Constructor
     * @param _owner The contract's owner
     */
    constructor(address _owner) {
        owner = _owner;
        emit NewOwner(_owner);
    }

    /**
     * @notice This function is used to cancel the ownership transfer.
     * @dev This function can be used for both cancelling a transfer to a new owner and
     *      cancelling the renouncement of the ownership.
     */
    function cancelOwnershipTransfer() external onlyOwner {
        Status _ownershipStatus = ownershipStatus;
        if (_ownershipStatus == Status.NoOngoingTransfer) {
            revert NoOngoingTransferInProgress();
        }

        if (_ownershipStatus == Status.TransferInProgress) {
            delete potentialOwner;
        }

        delete ownershipStatus;

        emit CancelOwnershipTransfer();
    }

    /**
     * @notice This function is used to confirm the ownership renouncement.
     */
    function confirmOwnershipRenouncement() external onlyOwner {
        if (ownershipStatus != Status.RenouncementInProgress) {
            revert RenouncementNotInProgress();
        }

        delete owner;
        delete ownershipStatus;

        emit NewOwner(address(0));
    }

    /**
     * @notice This function is used to confirm the ownership transfer.
     * @dev This function can only be called by the current potential owner.
     */
    function confirmOwnershipTransfer() external {
        if (ownershipStatus != Status.TransferInProgress) {
            revert TransferNotInProgress();
        }

        if (msg.sender != potentialOwner) {
            revert WrongPotentialOwner();
        }

        owner = msg.sender;
        delete ownershipStatus;
        delete potentialOwner;

        emit NewOwner(msg.sender);
    }

    /**
     * @notice This function is used to initiate the transfer of ownership to a new owner.
     * @param newPotentialOwner New potential owner address
     */
    function initiateOwnershipTransfer(address newPotentialOwner) external onlyOwner {
        if (ownershipStatus != Status.NoOngoingTransfer) {
            revert TransferAlreadyInProgress();
        }

        ownershipStatus = Status.TransferInProgress;
        potentialOwner = newPotentialOwner;

        /**
         * @dev This function can only be called by the owner, so msg.sender is the owner.
         *      We don't have to SLOAD the owner again.
         */
        emit InitiateOwnershipTransfer(msg.sender, newPotentialOwner);
    }

    /**
     * @notice This function is used to initiate the ownership renouncement.
     */
    function initiateOwnershipRenouncement() external onlyOwner {
        if (ownershipStatus != Status.NoOngoingTransfer) {
            revert TransferAlreadyInProgress();
        }

        ownershipStatus = Status.RenouncementInProgress;

        emit InitiateOwnershipRenouncement();
    }

    function _onlyOwner() private view {
        if (msg.sender != owner) revert NotOwner();
    }
}

contracts/libraries/PackableReentrancyGu.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

// Interfaces
import {IReentrancyGuard} from "../interfaces/IReentrancyGuard.sol";

/**
 * @title PackableReentrancyGuard
 * @notice This contract protects against reentrancy attacks.
 *         It is adjusted from OpenZeppelin.
 *         The only difference between this contract and ReentrancyGuard
 *         is that _status is uint8 instead of uint256 so that it can be
 *         packed with other contracts' storage variables.
 * @author LooksRare protocol team (ðŸ‘€,ðŸ’Ž)
 */
abstract contract PackableReentrancyGuard is IReentrancyGuard {
    uint8 private _status;

    /**
     * @notice Modifier to wrap functions to prevent reentrancy calls.
     */
    modifier nonReentrant() {
        if (_status == 2) {
            revert ReentrancyFail();
        }

        _status = 2;
        _;
        _status = 1;
    }

    constructor() {
        _status = 1;
    }
}

contracts/libraries/Pausable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/**
 * @title Pausable
 * @notice This contract makes it possible to pause the contract.
 *         It is adjusted from OpenZeppelin.
 * @author LooksRare protocol team (ðŸ‘€,ðŸ’Ž)
 */
abstract contract Pausable {
    /**
     * @dev Emitted when the pause is triggered by `account`.
     */
    event Paused(address account);

    /**
     * @dev Emitted when the pause is lifted by `account`.
     */
    event Unpaused(address account);

    error IsPaused();
    error NotPaused();

    bool private _paused;

    /**
     * @dev Modifier to make a function callable only when the contract is not paused.
     *
     * Requirements:
     *
     * - The contract must not be paused.
     */
    modifier whenNotPaused() {
        _requireNotPaused();
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is paused.
     *
     * Requirements:
     *
     * - The contract must be paused.
     */
    modifier whenPaused() {
        _requirePaused();
        _;
    }

    /**
     * @dev Returns true if the contract is paused, and false otherwise.
     */
    function paused() public view virtual returns (bool) {
        return _paused;
    }

    /**
     * @dev Throws if the contract is paused.
     */
    function _requireNotPaused() internal view virtual {
        if (paused()) {
            revert IsPaused();
        }
    }

    /**
     * @dev Throws if the contract is not paused.
     */
    function _requirePaused() internal view virtual {
        if (!paused()) {
            revert NotPaused();
        }
    }

    /**
     * @dev Triggers stopped state.
     *
     * Requirements:
     *
     * - The contract must not be paused.
     */
    function _pause() internal virtual whenNotPaused {
        _paused = true;
        emit Paused(msg.sender);
    }

    /**
     * @dev Returns to normal state.
     *
     * Requirements:
     *
     * - The contract must be paused.
     */
    function _unpause() internal virtual whenPaused {
        _paused = false;
        emit Unpaused(msg.sender);
    }
}

contracts/Lottery.sol.content:
// Lottery contract

// ennter lottery anonymously
// buy ticket with usdt
// generate random ticket id by normal random function
// pick a random winning ticket id (VRF Chainlink Oracle) verifiable random function
// automatic every interval  -> automatic make

// random pick -> chainlink oracle -> verifiable random function
// automation -> chainlink keepers -> smart contract

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Imports
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/KeeperCompatibleInterface.sol";

// Errors -> gas efficient way to revert
error Lottery__NotEnoughETHTrasferred();
error Lottery__TrasferFailed();
error Lottery__NotOpen();
error Lottery__UpkeepNotNeeded(
    uint256 balance,
    uint256 lotteryState,
    uint256 playerCount
);

/**
 * @title Lottery Contract
 * @author Gouldian
 * @dev This implements Chainlink VRF v2 and Chainlink Keepers
 */
contract Lottery is VRFConsumerBaseV2, KeeperCompatibleInterface, Ownable {
    IERC20 public USDTInterface;

    // Enum declaration here
    enum LotteryState {
        PENDING,
        OPEN,
        CLOSED,
        DONE
    } // enum -> list of values uint256 0 , 1

    // State variables
    address payable private a_feeCollector;
    uint256 private s_ticketSupply;
    uint256 private s_ticketRemaining;
    uint256 private s_ticketPrice;
    // uint256 private s_ticketFee;

    VRFCoordinatorV2Interface private immutable i_vrfCoordinator;
    bytes32 private immutable i_gasLane_keyHash;
    uint64 private immutable i_subscriptionId;
    uint32 private immutable i_callbackGasLimit;

    uint16 private constant REQUEST_CONFIRMATIONS = 5; // 5 blocks for confirmation
    uint16 private constant NUM_WORDS = 1; // 1 word

    mapping(uint => uint) private assignOrders;

    // Lottery variables
    uint256 private s_lotteryId;
    uint256 private s_lotteryWinningId;
    uint256 private s_latestRequestId;
    address payable private s_lotteryWinner;
    uint256 private s_lastLotteryTime;
    uint256 private immutable i_lotteryInterval;

    LotteryState private s_lotteryState;    
    address[] private s_players;
    mapping(uint256 => mapping(address => uint256[])) playerInfos;
    mapping(uint256 => mapping(uint256 => address payable)) ticketInfos;

    // Constants
    // uint256 private constant TICKET_SUPPLY = 1_000_000;
    uint256 private constant TICKET_SUPPLY = 50;
    uint256 private constant TICKET_LIMIT = 1000;
    uint256 private constant TICKET_PRICE = 4e6;    // 4 USDT
    uint256 private constant TICKET_FEE = 500;      // Fee is 5% (0.2 USDT) per each ticket
    address private zeroAddress = address(0x0000000000000000000000000000);

    // Events
    event lotteryOpen_event();
    event lotteryClose_event();
    event lottery_event(address indexed player);
    event buyTicket_event(address indexed player, uint256 amount);
    event RequestLotteryWinner(uint256 indexed requestId);
    event WinnerPicked(address indexed winner, uint256 winningTicketId);
    event withdrawPrize_event(address indexed winner, uint256 prize);

    // constructor
    constructor(
        address vrfCoordinatorV2,
        bytes32 gasLane,
        uint64 subscriptionId,
        uint32 callbackGasLimit,
        uint256 lotteryInterval,
        address usdtInterface
    ) VRFConsumerBaseV2(vrfCoordinatorV2) {
        require(usdtInterface != address(0), "Zero USDT Interface Address");

        i_vrfCoordinator = VRFCoordinatorV2Interface(vrfCoordinatorV2);
        i_gasLane_keyHash = gasLane;
        i_subscriptionId = subscriptionId;
        i_callbackGasLimit = callbackGasLimit;
        i_lotteryInterval = lotteryInterval;

        s_lotteryState = LotteryState.PENDING;
        s_ticketSupply = TICKET_SUPPLY;
        s_ticketPrice = TICKET_PRICE;
        // s_ticketFee = TICKET_PRICE * TICKET_FEE / 10000; // 0.05% of price is 2 USDT

        s_lotteryId = 0;

        USDTInterface = IERC20(usdtInterface);
        a_feeCollector = payable(msg.sender);
    }

    function startLottery() external onlyOwner {
        require(s_lotteryState == LotteryState.DONE || s_lotteryState == LotteryState.PENDING, "Lottery Not Closed");

        s_ticketRemaining = TICKET_SUPPLY;
        s_lotteryState = LotteryState.OPEN;
        s_lastLotteryTime = block.timestamp;
        s_players = new address[](0);

        emit lotteryOpen_event();
    }

    function closeLottery() external onlyOwner {
        _closeLottery();
    }

    function _closeLottery() internal {
        require(s_lotteryState == LotteryState.OPEN, "Lottery Not Opened");

        getRandomNumber();

        emit lotteryClose_event();
    }


    // Functions
    function buyTicket(uint64 _amountOfTickets) public {
        // 1. check if the lottery is open and ticket amount is available
        require(s_lotteryState == LotteryState.OPEN, "Lottery Not Opened");
        require(_amountOfTickets <= s_ticketRemaining, 'Not enough tickets remaining');
        require(playerInfos[s_lotteryId][msg.sender].length + _amountOfTickets <= TICKET_LIMIT, 'Exceed ticket max limit');

        // 2. check if the player has enough usdt
        uint256 usdtAmount = s_ticketPrice * _amountOfTickets;
        uint256 fee = usdtAmount * TICKET_FEE / 10000;

        require(usdtAmount + fee <= USDTInterface.allowance(msg.sender, address(this)), "Not enough USDT");

        // 3. send usdt to contract
        USDTInterface.transferFrom(msg.sender, address(this), usdtAmount);
        USDTInterface.transferFrom(msg.sender, a_feeCollector, fee);

        // 4. buy tickets with random numbers
        for(uint8 i = 0 ; i < _amountOfTickets ; i ++) {
            randomBuyTicket();
        }

        s_players.push(msg.sender);

        emit buyTicket_event(msg.sender, _amountOfTickets);
    }

    function getRandomNumber() internal {
        s_lotteryState = LotteryState.CLOSED;

        s_latestRequestId = i_vrfCoordinator.requestRandomWords(
            i_gasLane_keyHash, // gase lane keyHash -> max gas price willing to pay for a requeist in wei : ID for off -chain VRF job
            i_subscriptionId, // subscriptionId
            REQUEST_CONFIRMATIONS,
            i_callbackGasLimit, // gas limit for the callback
            NUM_WORDS // number of random words to return 1 word
        );
        emit RequestLotteryWinner(s_latestRequestId);
    }

    /**
     * @dev This is the function that the Chainlink Keeper will call to check if upkeep is needed.
     * @dev they look for 'upkeepNeeded' to be true
     * if condition match then returns true:
     * 1. lottery -> open state
     * 2. all ticket is sold out
     */
    function checkUpkeep(
        bytes memory /* checkData */
    )
        public
        view
        override
        returns (bool upkeepNeeded, bytes memory /* performData */)
    {
        bool isOpen = (s_lotteryState == LotteryState.OPEN);
        bool enoughTime = ((block.timestamp - s_lastLotteryTime) > i_lotteryInterval);
        upkeepNeeded = (isOpen && enoughTime && s_ticketRemaining == 0);
    }

    function performUpkeep(bytes calldata /* performData */) external override {
        // validate if the upkeep is needed
        (bool upkeepNeeded, ) = checkUpkeep("");
        if (!upkeepNeeded) {
            revert Lottery__UpkeepNotNeeded(
                address(this).balance,
                uint256(s_lotteryState),
                getPlayerCount()
            );
        }
        _closeLottery();
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        require(s_latestRequestId == requestId, "Wrong requestId");

        s_lotteryWinningId = randomWords[0] % s_ticketSupply;
        address payable winner = ticketInfos[s_lotteryId][s_lotteryWinningId];
        s_lotteryWinner = winner;

        s_players = new address[](0);
        s_lotteryState = LotteryState.DONE;
        s_lastLotteryTime = block.timestamp;
        s_lotteryId = s_lotteryId + 1;

        emit WinnerPicked(winner, s_lotteryWinningId); // to get a history of winners using events
    }

    function randomBuyTicket() internal {
        uint randIndex = _random() % s_ticketRemaining;
        uint ticketId = _fillAssignOrder(--s_ticketRemaining, randIndex) + 1;

        playerInfos[s_lotteryId][msg.sender].push(ticketId);
        ticketInfos[s_lotteryId][ticketId] = payable(msg.sender);
    }

    /**
     * @dev Generated the random ticket number.
     */
    function _random() internal view returns(uint) {
        return uint(
            keccak256(
                abi.encodePacked(block.timestamp + block.prevrandao
                    + ((uint(keccak256(abi.encodePacked(block.coinbase)))) / block.timestamp)
                    + block.gaslimit + ((uint(keccak256(abi.encodePacked(msg.sender)))) / block.timestamp)
                    + block.number)
            )
        ) / s_ticketRemaining;
    }

    /**
     * @dev Check and regenerated the random ticket number.
     */
    function _fillAssignOrder(uint orderA, uint orderB) internal returns(uint) {
        uint temp = orderA;
        if (assignOrders[orderA] != 0) temp = assignOrders[orderA];
        assignOrders[orderA] = orderB;
        if (assignOrders[orderB] != 0) assignOrders[orderA] = assignOrders[orderB];
        assignOrders[orderB] = temp;
        return assignOrders[orderA];
    }

    // Set function
    function setUSDTInterface(address _usdtInterface) external onlyOwner {
        require(_usdtInterface != address(0), "Zero USDT Interface Address");

        USDTInterface = IERC20( _usdtInterface);
    }

    function setFeeCollector(address payable _feeCollector) external onlyOwner {
        require(_feeCollector != address(0));
        a_feeCollector = _feeCollector;
    }

    function getFeeCollector() public view returns(address) {
        return a_feeCollector;
    }

    function getTicketPrice() public view returns (uint256) {
        return s_ticketPrice;
    }

    function getTicketFee() public view returns (uint256) {
        return TICKET_PRICE * TICKET_FEE / 10000;
    }

    function getPlayerCount() public view returns (uint256) {
        return s_players.length;
    }

    function getPlayer(uint256 index) public view returns (address) {
        return s_players[index];
    }

    function getPlayerInfo(address _player) public view returns(uint256[] memory) {
        return playerInfos[s_lotteryId][_player];
    }

    function getWinner() public view returns (address) {
        return s_lotteryWinner;
    }

    function getWinningId() public view returns (uint256) {
        return s_lotteryWinningId;
    }

    function getLotteryState() public view returns (LotteryState) {
        return s_lotteryState;
    }

    function getCurrentLotteryID() public view returns (uint256) {
        return s_lotteryId;
    }

    function getNumWord() public pure returns (uint256) {
        return NUM_WORDS;
    }

    function getLastTimeStemp() public view returns (uint256) {
        return s_lastLotteryTime;
    }

    function getRequestConfirmations() public pure returns (uint16) {
        return REQUEST_CONFIRMATIONS; // number of confirmations to wait for before calling the callback
    }

    function getLotteryInterval() public view returns (uint256) {
        return i_lotteryInterval;
    }

    function getRemainingTicketAmount() public view returns (uint256) {
        return s_ticketRemaining;
    }

    function withdrawPrize() public {
        require(s_lotteryWinner != address(0), "No picked winner yet");
        require(msg.sender == s_lotteryWinner, "Not a winner");

        uint256 usdtBalance = USDTInterface.balanceOf(address(this));
        require(USDTInterface.approve(address(this), usdtBalance), "USDT approve failed");

        USDTInterface.transferFrom(address(this), msg.sender, usdtBalance);

        // address zeroAddress = new address(0);
        s_lotteryWinner = payable(zeroAddress);

        emit withdrawPrize_event(s_lotteryWinner, usdtBalance);
    }
}

